/**
 * Safe interval utility with visibility pause and max iterations
 * @param {Function} tick - Function to call on each interval
 * @param {number} ms - Interval duration in milliseconds
 * @param {number} maxIterations - Maximum number of iterations (default: 120)
 * @returns {Function} Cleanup function to stop the interval
 */
export function safeInterval(tick, ms, maxIterations = 120) {
  let n = 0
  let id

  const step = () => {
    tick()
    if (++n >= maxIterations) {
      clearInterval(id)
    }
  }

  const vis = () => {
    if (document.hidden) {
      clearInterval(id)
    } else {
      clearInterval(id)
      id = setInterval(step, ms)
    }
  }

  document.addEventListener('visibilitychange', vis)
  vis()

  return () => {
    clearInterval(id)
    document.removeEventListener('visibilitychange', vis)
  }
}

/**
 * Motion Utilities - Issue #470
 * Provides motion governance with reduced-motion support and IntersectionObserver
 */

export const prefersReducedMotion = () => {
  return window.matchMedia('(prefers-reduced-motion: reduce)').matches
}

export const getAnimationVariants = (type = 'fade') => {
  if (prefersReducedMotion()) {
    return {
      initial: { opacity: 1 },
      animate: { opacity: 1 },
      exit: { opacity: 1 }
    }
  }

  const variants = {
    fade: {
      initial: { opacity: 0 },
      animate: { opacity: 1 },
      exit: { opacity: 0 }
    },
    slideUp: {
      initial: { opacity: 0, y: 20 },
      animate: { opacity: 1, y: 0 },
      exit: { opacity: 0, y: -20 }
    },
    slideDown: {
      initial: { opacity: 0, y: -20 },
      animate: { opacity: 1, y: 0 },
      exit: { opacity: 0, y: 20 }
    },
    slideLeft: {
      initial: { opacity: 0, x: 20 },
      animate: { opacity: 1, x: 0 },
      exit: { opacity: 0, x: -20 }
    },
    slideRight: {
      initial: { opacity: 0, x: -20 },
      animate: { opacity: 1, x: 0 },
      exit: { opacity: 0, x: 20 }
    },
    scale: {
      initial: { opacity: 0, scale: 0.95 },
      animate: { opacity: 1, scale: 1 },
      exit: { opacity: 0, scale: 0.95 }
    }
  }

  return variants[type] || variants.fade
}

export const getTransition = (duration = 0.3, delay = 0) => {
  if (prefersReducedMotion()) {
    return { duration: 0 }
  }

  return {
    duration: Math.min(duration, 0.6), // Max 600ms per guidelines
    delay,
    ease: [0.4, 0, 0.2, 1] // Smooth easing
  }
}

export class AnimationObserver {
  constructor(options = {}) {
    this.options = {
      threshold: options.threshold || 0.1,
      rootMargin: options.rootMargin || '0px',
      triggerOnce: options.triggerOnce !== false
    }
    this.observer = null
    this.elements = new Map()
  }

  observe(element, callback) {
    if (!element || prefersReducedMotion()) {
      if (callback) callback(element, true)
      return
    }

    if (!this.observer) {
      this.observer = new IntersectionObserver((entries) => {
        entries.forEach(entry => {
          const callback = this.elements.get(entry.target)
          if (callback && entry.isIntersecting) {
            callback(entry.target, true)
            if (this.options.triggerOnce) {
              this.unobserve(entry.target)
            }
          }
        })
      }, {
        threshold: this.options.threshold,
        rootMargin: this.options.rootMargin
      })
    }

    this.elements.set(element, callback)
    this.observer.observe(element)
  }

  unobserve(element) {
    if (this.observer && element) {
      this.observer.unobserve(element)
      this.elements.delete(element)
    }
  }

  disconnect() {
    if (this.observer) {
      this.observer.disconnect()
      this.elements.clear()
    }
  }
}

export const useScrollAnimation = (ref, options = {}) => {
  const [isVisible, setIsVisible] = React.useState(false)

  React.useEffect(() => {
    const element = ref.current
    if (!element) return

    const observer = new AnimationObserver(options)
    observer.observe(element, () => {
      setIsVisible(true)
    })

    return () => observer.disconnect()
  }, [ref, options])

  return isVisible
}

let activeAnimations = 0
const MAX_ANIMATIONS = 3

export const requestAnimation = () => {
  if (prefersReducedMotion()) return false
  if (activeAnimations >= MAX_ANIMATIONS) return false

  activeAnimations++
  return true
}

export const releaseAnimation = () => {
  activeAnimations = Math.max(0, activeAnimations - 1)
}

export const withMotionBudget = (Component) => {
  return (props) => {
    const canAnimate = requestAnimation()

    React.useEffect(() => {
      return () => releaseAnimation()
    }, [])

    if (!canAnimate) {
      return <Component {...props} animate={false} />
    }

    return <Component {...props} />
  }
}

export const getMotionClass = (animationClass) => {
  if (prefersReducedMotion()) {
    return ''
  }
  return animationClass
}

export default {
  prefersReducedMotion,
  getAnimationVariants,
  getTransition,
  AnimationObserver,
  useScrollAnimation,
  requestAnimation,
  releaseAnimation,
  withMotionBudget,
  getMotionClass
}

/**
 * Focus Management Utilities for WCAG 2.1 AA Compliance
 * Provides utilities for managing focus, keyboard navigation, and focus trapping
 */

/**
 * Trap focus within a container (for modals/dialogs)
 * @param {HTMLElement} container - The container element to trap focus within
 * @returns {Function} Cleanup function to remove event listeners
 */
export const trapFocus = (container) => {
  if (!container) return () => {}

  const focusableElements = container.querySelectorAll(
    'a[href], button:not([disabled]), textarea:not([disabled]), input:not([disabled]), select:not([disabled]), [tabindex]:not([tabindex="-1"])'
  )

  const firstFocusable = focusableElements[0]
  const lastFocusable = focusableElements[focusableElements.length - 1]

  const handleKeyDown = (e) => {
    if (e.key !== 'Tab') return

    if (e.shiftKey) {
      // Shift + Tab
      if (document.activeElement === firstFocusable) {
        e.preventDefault()
        lastFocusable?.focus()
      }
    } else {
      // Tab
      if (document.activeElement === lastFocusable) {
        e.preventDefault()
        firstFocusable?.focus()
      }
    }
  }

  container.addEventListener('keydown', handleKeyDown)

  // Focus first element
  firstFocusable?.focus()

  return () => {
    container.removeEventListener('keydown', handleKeyDown)
  }
}

/**
 * Store and restore focus (useful for modals)
 */
export class FocusManager {
  constructor() {
    this.previousFocus = null
  }

  saveFocus() {
    this.previousFocus = document.activeElement
  }

  restoreFocus() {
    if (this.previousFocus && typeof this.previousFocus.focus === 'function') {
      this.previousFocus.focus()
    }
  }
}

/**
 * Get all focusable elements within a container
 * @param {HTMLElement} container
 * @returns {NodeList}
 */
export const getFocusableElements = (container) => {
  if (!container) return []

  return container.querySelectorAll(
    'a[href], button:not([disabled]), textarea:not([disabled]), input:not([disabled]), select:not([disabled]), [tabindex]:not([tabindex="-1"])'
  )
}

/**
 * Check if element is visible and focusable
 * @param {HTMLElement} element
 * @returns {boolean}
 */
export const isFocusable = (element) => {
  if (!element) return false

  const style = window.getComputedStyle(element)
  return (
    style.display !== 'none' &&
    style.visibility !== 'hidden' &&
    !element.hasAttribute('disabled') &&
    element.tabIndex !== -1
  )
}

/**
 * Move focus to next/previous focusable element
 * @param {HTMLElement} container
 * @param {boolean} reverse - Move backwards if true
 */
export const moveFocus = (container, reverse = false) => {
  const focusableElements = Array.from(getFocusableElements(container))
  const currentIndex = focusableElements.indexOf(document.activeElement)

  let nextIndex = reverse ? currentIndex - 1 : currentIndex + 1

  if (nextIndex < 0) nextIndex = focusableElements.length - 1
  if (nextIndex >= focusableElements.length) nextIndex = 0

  focusableElements[nextIndex]?.focus()
}

/**
 * Add visible focus indicator to element
 * @param {HTMLElement} element
 */
export const addFocusIndicator = (element) => {
  if (!element) return

  element.style.outline = '2px solid #3b82f6'
  element.style.outlineOffset = '2px'
}

/**
 * Remove focus indicator from element
 * @param {HTMLElement} element
 */
export const removeFocusIndicator = (element) => {
  if (!element) return

  element.style.outline = ''
  element.style.outlineOffset = ''
}

